def add_data():
    for obj in EQUIPMENT:
        equip.insert_one({
            'class_code': obj[0],
            'class_of': obj[1],
            'type_code': obj[2],
            'type_of': obj[3],
            'part_code': obj[4],
            'part_of': obj[5],
            'cipher': obj[6],
        })
    for obj in REASONS:
        reasons.insert_one({
            'group_code': obj[0],
            'group': obj[1],
            'reason_code': obj[2],
            'reason': obj[3],
            'cipher': obj[4],
        })
    for obj in TYPE_OF.values():
        type_of, codes = obj
        for code in codes:
            description = CATEGORIES.get(code)
            bounces.insert_one({
                'type_of': type_of,
                'code': code,
                'description': description
            })


add_data()
